import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'transactions';

  customers:any[]=[
    {
    "id": 1,
    "name": "Ahmed Ali"
    },
    {
    "id": 2,
    "name": "Aya Elsayed"
    },

    {
    "id": 3,
    "name": "Mina Adel"
    },
    {
    "id": 4,
    "name": "Sarah Reda"
    },
    {
    "id": 5,
    "name": "Mohamed Sayed"
    }
    ]
    transactions:any[]=[
      {
      "id": 1,
      "customer_id": 1,
      "date": "2022-01-01",
      "amount": 1000
      },
      {
        "id": 11,
        "customer_id": 1,
        "date": "2022-01-05",
        "amount": 2000
      },
      {
      "id": 2,
      "customer_id": 1,
      "date": "2022-01-10",
      "amount": 600
      },
      {
      "id": 3,
      "customer_id": 2,
      "date": "2022-01-01",
      "amount": 550
      },
      {
      "id": 4,
      "customer_id": 3,
      "date": "2022-01-01",
      "amount": 500
      },
      {
      "id": 5,

      "customer_id": 2,
      "date": "2022-01-02",
      "amount": 1300
      },
      {
      "id": 6,
      "customer_id": 4,
      "date": "2022-01-01",
      "amount": 750
      },
      {
      "id": 7,
      "customer_id": 3,
      "date": "2022-01-02",
      "amount": 1250
      },
      {
      "id": 8,
      "customer_id": 5,
      "date": "2022-01-01",
      "amount": 2500
      },
      {
      "id": 9,
      "customer_id": 5,
      "date": "2022-01-02",
      "amount": 875
      },
      {
      "id": 10,
      "customer_id": 4,
      "date": "2022-01-03",
      "amount": 1500
      }
      ]
      customerName:string=""
      dates:string[]=[];
      transactionsAmounts:number[]=[]

      constructor(){
       this.getAllUsersTransaction()
      }

      getAllUsersTransaction()
      {
        this.customers = this.customers.map((customer)=>{
          return {
            id:customer.id,
            name: customer.name,
            transactions:this.transactions.filter((transaction)=> transaction.customer_id === customer.id),
            totalTransactionsAmount: this.transactions.filter((transaction)=> transaction.customer_id === customer.id).reduce((sum,transaction)=>sum+transaction.amount,0)
          }
        })
        console.log(this.customers)
      }
      viewAllTransActions(transactions:any,customerName:string)
      {
        this.customerName=customerName
        this.dates = []
        this.transactionsAmounts=[]
        console.log(transactions);
        transactions.forEach((transaction:any) => {
          this.dates.push(transaction.date),
          this.transactionsAmounts.push(transaction.amount)
        })
       this.viewGraphDetails()
      }

      data: any;

    options: any;

    ngOnInit() {

    }
    viewGraphDetails()
    {
      const documentStyle = getComputedStyle(document.documentElement);
      const textColor = documentStyle.getPropertyValue('--text-color');
      const textColorSecondary = documentStyle.getPropertyValue('--text-color-secondary');
      const surfaceBorder = documentStyle.getPropertyValue('--surface-border');

      this.data = {
          labels: this.dates,
          datasets: [
              {
                  label: this.customerName,
                  data: this.transactionsAmounts,
                  fill: false,
                  borderColor: documentStyle.getPropertyValue('--blue-500'),
                  tension: 0.4
              }

          ]
      };

      this.options = {
          maintainAspectRatio: false,
          aspectRatio: 0.6,
          plugins: {
              legend: {
                  labels: {
                      color: textColor
                  }
              }
          },
          scales: {
              x: {
                  ticks: {
                      color: textColorSecondary
                  },
                  grid: {
                      color: "red",
                      drawBorder: false
                  }
              },
              y: {
                  ticks: {
                      color: textColorSecondary
                  },
                  grid: {
                      color: surfaceBorder,
                      drawBorder: false
                  }
              }
          }
      };
    }
}
